import React from 'react';
import mapMarketing from '../images/map-maker.svg';
import { Link } from 'react-router-dom';
import { FiPlus } from 'react-icons/fi';
import '../styles/pages/orphanages-map.css';
import { Map,TileLayer, Marker } from 'react-leaflet';
import Leaflet from 'leaflet';

import 'leaflet/dist/leaflet.css';

const mapIcon = Leaflet.icon({
    iconUrl
})

function OrphanagesMap() {
    return (
        <div id="page-map">
            <aside>
                <header>
                    <img src={mapMarketing} alt="Happy"/>

                    <h2>Escolha um orfanato do mapa</h2>

                    <p>Muitas crianças estão esperando a sua visita :)</p>
                </header>

                <footer>
                    <strong>Rio do Sul</strong>
                    <span>Santa Catarina</span>
                </footer>
            </aside>

            <Map 
            center={[-22.7685953,-45.1969024]}
            zoom={15}
            style={{width:'100%', height:'100%'}}
            >
                <TileLayer url="http://a.tile.openstreetmap.org/{z}/{x}/{y}.png"/>

                <Marker
                    position={[-22.7685953,-45.1969024]}
                />
            </Map>

            <Link to="" className="create-orphanage">
                <FiPlus size={32} color="#FFF"/>
            </Link>
        </div>
    );
}

export default OrphanagesMap;
